package com.tns.ifet.daytwo;

public class charDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'a';
		System.out.println("Character is: "+ch);
		char ch1 = 65;
		System.out.println("Number to char: "+ch1);
		char  var = '\u00A7';
		System.out.println("Unicode to char: "+var);
		char var1 = '\u20AC';
		System.out.println("Unicode: "+var1);
		int a ='A';
		System.out.println("ASCI code: "+a);
	}

}
